import { useState } from "react";

export function Product() {
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [selectedtext, setText] = useState("");

  const handleCategoryChange = (e) => {
    setSelectedCategory(e.target.value);
    setText(""); 
  };
  const handleSerach = (value) => {
    setText(value);
  };

  
  const filteredByCategory = selectedCategory === "all"
    ? Things
    : Things.filter((item) => item.category === selectedCategory);


  const yooo = selectedtext
    ? filteredByCategory.filter((item) =>
        item.Name.toLowerCase().includes(selectedtext.toLowerCase())
      )
    : filteredByCategory;

  return (
    <>
      <div id="selectionDiv">
        <label htmlFor="selection">Category :</label>
        <select
          name="Sort"
          id="selection"
          value={selectedCategory}
          onChange={handleCategoryChange}
        >
          <option value="all">All Categories</option>
          <option value="graphicsCard">Graphics Card</option>
          <option value="monitor">Monitor</option>
          <option value="mouse">Mouse</option>
          <option value="keyboard">Keyboard</option>
          <option value="shoes">Shoes</option>
        </select>
      </div>
      <div id="searchh">
        <label htmlFor="yoo">Search: </label>
        <input
          id="inputt"
          placeholder="Search item..."
          type="text"
          value={selectedtext}
          onChange={(e) => handleSerach(e.target.value)}
        />
      </div>
      <div className="Totalbox">
        {yooo.length > 0 ? (
          yooo.map((item) => (
            <div key={item.id} className="eachBorder">
              <div className="image">
                <img
                  src={item.image || "https://via.placeholder.com/100"}
                  alt={item.Name}
                />
              </div>
              <br />
              Item: {item.Name} <br />
              <br />
              Price: {item.price}{" "}
            </div>
          ))
        ) : (
          <div style={{ padding: "1rem", color: " white" }}> item not found</div>
        )}
      </div>
    </>
  );
}
